// File: Fraction.java// Author: Seth Roller// Date: 4/3/18// Purpose: To perform fraction calculationsimport java.util.*;  // Scannerimport java.io.*;    // PrintStreampublic class Fraction extends Object{    private int myNumerator;    private int myDenominator;        //********************************************************    // Fraction default-value constructor.                   *    // Precondition: A Fraction object has been declared.    *    // Postcondition: myNumerator == 0 && myDenominator == 1.*    //********************************************************        public Fraction()    {	myNumerator = 0;	myDenominator = 1;    }    public void update(int num, int den)    {	if (den == 0)	    throw new IllegalArgumentException("In Fraction, denominator"					       + " cannot be zero.");	myNumerator = num;	myDenominator = den;    }         //********************************************************    // Fraction explicit-value constructor.                  *    // Precondition: A Fraction object has been declared.    *    // Receive: numerator, denominator, two integers.        *    // Postcondition: myNumerator == numerator &&            *    //                 myDenominator == denominator.         *    //********************************************************        public Fraction(int num, int den)    {        update(num, den);    }		    //********************************************************    // Numerator accessor                                    *    // Return: the value of myNumerator.                     *    //********************************************************    public int getNumerator()    {	return myNumerator;    }        //********************************************************    // Denominator accessor                                  *    // Return: the value of myDenominator.                   *    //********************************************************    public int getDenominator()    {	return myDenominator;    }        //********************************************************    // Input function member                                 *    // Precondition: in contains a Fraction n/d.             *    // Input: in, a Scanner .                                *    // Passback: in, with fraction read                      *    // Postcondition: myNumerator == n && myDenominator == d.*    //********************************************************    // fix this    public void read(Scanner in)    {	    	    	String fract = in.next();	StringTokenizer parser = new StringTokenizer(fract, "/",false);	if (parser.countTokens() != 2)	    throw new RuntimeException("Bad format of fraction.");	int myNum = Integer.parseInt(parser.nextToken());	int myDen = Integer.parseInt(parser.nextToken());	update(myNum, myDen);	    }        //********************************************************    // multiplication                                        *    // Receive: rightOperand, a Fraction object.             *    // Return: result, the product of the receiver of        *    //          this message and rightOperand.               *    //********************************************************    public Fraction times(Fraction rightOperand)    {	Fraction result;	result = new Fraction(myNumerator * rightOperand.getNumerator(),			      myDenominator *			      rightOperand.getDenominator());	result.simplify();	return result;    }	    //********************************************************    // Simplify a Fraction from improper to proper.          *    // Postcondition: the Fraction receiving this message    *    //                 is a proper fraction.                 *    //********************************************************    public void simplify()    {	int gcd;	while (true)	{	    gcd = greatestCommonDivisor(myNumerator, myDenominator);	    if (gcd == 1)		return;	    myNumerator = myNumerator / gcd;	    myDenominator = myDenominator / gcd;	}    }			    //********************************************************    // override super classes toString                       *    // Return: String equivalent of fraction                 *    //********************************************************    public String toString()    {	String retval = "";	retval = myNumerator + "/" + myDenominator;	return retval;    }        // **********************************************************    // greatestCommonDivisor finds the greatest common divisor  *    // of two integers, using Euclid's (recursive) algorithm.   *    //                                                          *    //  Receive: alpha, beta, two integers.                     *    //  Return: the greatest common divisor of alpha and beta.  *    //***********************************************************	private static int greatestCommonDivisor(int alpha, int beta)	{		alpha = Math.abs(alpha);		// take absolute values of operands		beta = Math.abs(beta);		if (beta == 0)       // base case			return alpha;		else                 // induction step		{			int remainder = alpha % beta;			return greatestCommonDivisor(beta, remainder);		}	}        //********************************************************    // addition                                              *    // Receive: rightOperand, a Fraction object.             *    // Return: result, the sum of the receiver of            *    //          this message and rightOperand.               *    //********************************************************    public Fraction add(Fraction rightOperand)    {	Fraction result;		result = new Fraction((myNumerator * rightOperand.getDenominator()			       ) + (rightOperand.getNumerator() *				    myDenominator), myDenominator *			      rightOperand.getDenominator());	result.simplify();	return result;    }    //********************************************************    // subtraction                                           *    // Receive: rightOperand, a Fraction object.             *    // Return: result, the difference of the receiver of     *    //          this message and rightOperand.               *    //********************************************************    public Fraction subtract(Fraction rightOperand)    {	Fraction result;	result = new Fraction((myNumerator * rightOperand.getDenominator()			       ) - (rightOperand.getNumerator() *				    myDenominator), myDenominator *			      rightOperand.getDenominator());	result.simplify();	return result;    }        //********************************************************    // division                                              *    // Receive: rightOperand, a Fraction object.             *    // Return: result, the quotient of the receiver of       *    //          this message and rightOperand.               *    //********************************************************    public Fraction divide(Fraction rightOperand)    {	Fraction result;	result = new Fraction(myNumerator * rightOperand.getDenominator()			      , myDenominator *			      rightOperand.getNumerator());	result.simplify();	return result;    }        //***********************************************************    // returns a boolean if the fraction is lessThan to    // the rightOperand    //***********************************************************    public boolean lessThan(Fraction second)    {		double frac2 = (second.getNumerator()) /	    (double)(second.getDenominator());		if ((myNumerator / (double)myDenominator < frac2))	    return true;	else	    return false;    }    //***********************************************************    // returns a boolean if the fraction is lessThanOrEqual to    // the rightOperand    //***********************************************************    public boolean lessThanOrEqual(Fraction rightOperand)    {	if ((myNumerator / (double)myDenominator) <=	    (rightOperand.getNumerator() /	     (double)rightOperand.getDenominator()))	    return true;	else	    return false;    }        //***********************************************************    // returns a boolean if the fraction is equalTo to    // the rightOperand    //***********************************************************        public boolean equalTo(Fraction rightOperand)    {	if ((myNumerator == rightOperand.getNumerator())	    && (myDenominator == rightOperand.getDenominator()))	{	    return true;	}	else	    return false;    }    //***********************************************************    // returns a boolean if the fraction is greaterThan to    // the rightOperand    //***********************************************************    public boolean greaterThan(Fraction rightOperand)    {	if ((myNumerator / (double)myDenominator) >	    (rightOperand.getNumerator() /	     (double)rightOperand.getDenominator()))	    return true;	else	    return false;    }        //***********************************************************    // returns a boolean if the fraction is greaterThanOrEqual to    // the rightOperand    //***********************************************************      public boolean greaterThanOrEqual(Fraction rightOperand)    {	if ((myNumerator / (double)myDenominator) >=	    (rightOperand.getNumerator() /	     (double)rightOperand.getDenominator()))	    return true;	else	    return false;    }    }